import './App.css';

import MeasurementPage from "./MeasurementPage";

import Box from "@mui/material/Box";
import React from "react";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import ResultsPage from "./ResultsPage";
import CssBaseline from "@mui/material/CssBaseline";

const PAGE_MEASUREMENT = "measurement";
const PAGE_RESULTS = "results";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currPage: "",
      measurements: {
        vals: [],
        dates: [],
      },
    };
  }

  setCurrPage(page) {
    this.setState({ currPage: page });
  }

  addMeasurement({ vals, date }) {
    let measurements = this.state.measurements;
    measurements.vals.push(vals);
    measurements.dates.push(date);
    this.setState({ measurements: measurements });
    this.updateData();
  }

  async login() {
    //TODO: sign in with google auth
    console.log("logging in");
    this.setCurrPage("");
  }

  async loadData() {
    //TODO: load from google drive
  }

  async updateData() {
    //TODO: update in google drive
    console.log(
      "TODO: push to google. Current Measurements: ",
      this.state.measurements
    );
  }

  getContents() {
    let currPage = this.state.currPage;
    if (currPage === PAGE_MEASUREMENT) {
      return <MeasurementPage addMeasurement={(m) => this.addMeasurement(m)} />;
    } else if (currPage === PAGE_RESULTS) {
      return <ResultsPage measurements={this.state.measurements} />;
    }
    return <Typography align="center">Select a page using the buttons above</Typography>;
  }

  render() {
    return (
      <Box>
        <CssBaseline />
        <AppBar
          position="fixed"
          sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}
        >
          <Toolbar>
            <Typography
              variant="h6"
              component="div"
              sx={{ marginRight: "25px" }}
            >
              MADSense Dashboard
            </Typography>
            <Box sx={{ flexGrow: 1 }}>
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => this.setCurrPage(PAGE_MEASUREMENT)}
              >
                Take Measurement
              </Button>
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => this.setCurrPage(PAGE_RESULTS)}
              >
                View Results
              </Button>
            </Box>
            <Button
              color="inherit"
              variant="outlined"
              onClick={() => this.login()}
            >
              Login
            </Button>
          </Toolbar>
        </AppBar>
        <Toolbar />
        {this.getContents()}
      </Box>
    );
  }
}
