import "./App.css";

import MeasurementPage from "./MeasurementPage";
import ResultsPage from "./ResultsPage";
import FinalResultsPage from "./FinalResultsPage";

import Box from "@mui/material/Box";
import React from "react";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import CssBaseline from "@mui/material/CssBaseline";
import logo from "./sensis_logo.png";
import xlsExport from "xlsexport"

const PAGE_MEASUREMENT = "measurement";
const PAGE_RESULTS = "results";
const PAGE_FINAL_RESULTS = "finalresults";

export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currPage: "",
      measurements: {
        lens: [],
        rawVals: [],
        vols: [],
        times: [],
        dates: [],
      },
    };
  }


  setCurrPage(page) {
    this.setState({ currPage: page });
  }

  addMeasurement({ lens, rawVals, vol, date, time }) {
    let measurements = this.state.measurements;
    measurements.lens.push(lens);
    measurements.rawVals.push(rawVals);
    measurements.vols.push(vol);
    measurements.dates.push(date);
    measurements.times.push(time)
    this.setState({ measurements: measurements });
    this.updateData();
  }

  exportToExcel(title) {
    let data = {
      chan1: this.state.measurements.rawVals[0], 
      chan2: this.state.measurements.rawVals[1], 
      chan3: this.state.measurements.rawVals[2], 
      chan4: this.state.measurements.rawVals[3], 
      chan5: this.state.measurements.rawVals[4], 
      chan6: this.state.measurements.rawVals[5], 
      chan7: this.state.measurements.rawVals[6], 
      chan8: this.state.measurements.rawVals[7],
      len1: this.state.measurements.lens[0], 
      len2: this.state.measurements.lens[1], 
      len3: this.state.measurements.lens[2], 
      len4: this.state.measurements.lens[3], 
      len5: this.state.measurements.lens[4], 
      len6: this.state.measurements.lens[5], 
      len7: this.state.measurements.lens[6], 
      len8: this.state.measurements.lens[7],
      vols:  this.state.measurements.vols,
      times: this.state.measurements.times,
      dates: this.state.measurements.dates,
    }
    const xls = new xlsExport(data,title);
    xls.exportToXls(title);
    console.log("Exported to excel at file ", title);
  }

  async login() {
    //TODO: sign in with google auth
    console.log("logging in");
    this.setCurrPage("");
  }

  async loadData() {
    //TODO: load from google drive
  }

  async updateData() {
    //TODO: update in google drive
    console.log(
      "Current Measurements: ",
      this.state.measurements
    );
  }

  getContents() {
    let currPage = this.state.currPage;
    if (currPage === PAGE_MEASUREMENT) {
      return <MeasurementPage addMeasurement={(m) => this.addMeasurement(m)} />;
    } else if (currPage === PAGE_RESULTS) {
      return <ResultsPage measurements={this.state.measurements} />;
    } else if (currPage === PAGE_FINAL_RESULTS) {
      return <FinalResultsPage measurements={this.state.measurements} />;
    }
    return (
      <Typography align="center">
        Select a page using the tabs above
      </Typography>
    );
  }


  render() {
    return (
      <Box>
        <CssBaseline />
        <AppBar
          position="fixed"
          sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}
        >
          <Toolbar>
            <img src={logo} width={50}></img>
            <Typography variant="h6" component="div" sx={{ mx: "25px" }}>
              Dashboard
            </Typography>
            <Box sx={{ flexGrow: 1 }}>
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => this.setCurrPage(PAGE_MEASUREMENT)}
              >
                Take Measurement
              </Button>
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => this.setCurrPage(PAGE_RESULTS)}
              >
                Sensor Results
              </Button>
              <Button
                variant="outlined"
                color="inherit"
                onClick={() => this.setCurrPage(PAGE_FINAL_RESULTS)}
              >
                Final Results
              </Button>
            </Box>
            <Button
              color="inherit"
              variant="outlined"
              onClick={() => this.login()}
            >
              Login
            </Button>
          </Toolbar>
        </AppBar>
        <Toolbar />
        {this.getContents()}
      </Box>
    );
  }
}
