import { Box, Button, Modal, Typography } from "@mui/material";
import React from "react";
//import p5ble from "p5ble";

// these are global constants right here, but might need to be moved to state variables
const receivedValues = ["s1: 0.0", "s2: 0.0", "s3: 0.0", "s4: 0.0", "s5: 0.0", "s6: 0.0", "s7: 0.0", "s8: 0.0"];
let isConnected = false;

function sleep(milliseconds) {
  const date = Date.now();
  let currentDate = null;
  do {
    currentDate = Date.now();
  } while (currentDate - date < milliseconds);
}

function takeMeasurement() {
  if (isConnected) {
    sendData("READ");
    sleep(1000);
  } else {
    console.log("Error: device not paired.")
  }

  let time = new Date().toLocaleDateString("en-US");
  console.log(receivedValues[0])
  console.log(receivedValues[1])
  console.log(receivedValues[2])
  console.log(receivedValues[3])
  console.log(receivedValues[4])
  console.log(receivedValues[5])
  console.log(receivedValues[6])
  console.log(receivedValues[7])

  return {
    vals: Array.from({ length: 7 }, () => Math.random() * 10),
    date: time,
  };
}

async function getBluetoothDevices() {
  let bluetoothAvailable = await navigator.bluetooth.getAvailability();
  if (bluetoothAvailable) {
    return await navigator.bluetooth.requestDevice({ acceptAllDevices: true });
  }
  //let blueTooth = new p5ble();
  //let blueToothCharacteristic; //this is a blu
  //blueTooth.connect(0xffe0, gotCharacteristics);
}

// A function that will be called once got characteristics
function gotCharacteristics(error, characteristics) {
  if (error) {
    console.log("error: ", error);
  }
  console.log("characteristics: ", characteristics);
  blueToothCharacteristic = characteristics[0];

  blueTooth.startNotifications(blueToothCharacteristic, gotValue, "string");

  isConnected = blueTooth.isConnected();
  // Add a event handler when the device is disconnected
  blueTooth.onDisconnected(onDisconnected);
}

// A function that will be called once got values
function gotValue(value) {
  console.log("value: ", value);
  receivedValues[value[0]] = value;
}

function onDisconnected() {
  console.log("Device got disconnected.");
  isConnected = false;
}

function sendData(command) {
  const inputValue = command;
  if (!("TextEncoder" in window)) {
    console.log("Sorry, this browser does not support TextEncoder...");
  }
  var enc = new TextEncoder(); // always utf-8
  blueToothCharacteristic.writeValue(enc.encode(inputValue));
}

export default function MeasurementPage(props) {
  const style = {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    width: 600,
    bgcolor: "background.paper",
    border: "2px solid #000",
    boxShadow: 24,
    p: 4,
  };
  let [open, setOpen] = React.useState(false);
  return (
    <Box>
      <Modal open={open} onClose={() => setOpen(false)}>
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
            Text in a modal
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
          </Typography>
        </Box>
      </Modal>
      <ol>
        <li>Put on the device</li>
        <li>TBD</li>
        <li>3rd thing here</li>
      </ol>
      <Button onClick={() => props.addMeasurement(takeMeasurement())}>
        Take Measurement!
      </Button>
      <Button onClick={() => getBluetoothDevices()}>Pair Device</Button>
    </Box>
  );
}
